import { Component, OnInit } from '@angular/core';
import { UserInfo } from '../_models/userInfo';
import { User } from '../_models/user';
import { AccountService } from '../_services/account.service';
import { UserInfoService } from '../_services/user-info.service';
import { take } from 'rxjs';
import { Job } from '../_models/job';

@Component({
  selector: 'app-user-home',
  templateUrl: './user-home.component.html',
  styleUrls: ['./user-home.component.css'],
})
export class UserHomeComponent implements OnInit {
  userInfo: UserInfo | undefined;
  user: User | null = null;
  jobs: Job[] = [];

  constructor(
    private accountService: AccountService,
    private userInfoService: UserInfoService
  ) {
    this.accountService.currentUser$.pipe(take(1)).subscribe({
      next: (user) => (this.user = user),
    });
  }

  ngOnInit(): void {
    this.loadUser();
    this.getJobsForUser();
  }

  loadUser() {
    if (!this.user) return;
    this.userInfoService.getUser(this.user.username).subscribe({
      next: (userInfo) => (this.userInfo = userInfo),
    });
    console.log('from user home', this.userInfo?.userName);
  }

  getJobsForUser() {
    if (!this.user) return;
    this.userInfoService.getJobsForUser2(this.user.username).subscribe({
      next: (jobs) => (this.jobs = jobs),
    });
  }
}
<div class="tw-mt-2" *ngIf="user">
  <h1 class="tw-text-3xl">Your Job Search List</h1>
  <div>
    <p>Welcome {{ user.username | titlecase }}</p>
    <p>Last login: {{ userInfo?.lastLoginDate | date }}</p>
  </div>
  <div>
    <ul>
      <li *ngFor="let job of jobs">
        <h3>{{ job.jobTitle }}</h3>
        <p>{{ job.jobDescription }}</p>
        <p>{{ job.companyName }}</p>
        <p>{{ job.city }}, {{ job.state }}</p>
        <p>{{ job.skillLevel }}</p>
        <p>{{ job.salaryRange }}</p>
        <p>{{ job.remoteHybridOnsite }}</p>
        <p>{{ job.urlListing }}</p>
        <p>{{ job.status }}</p>
      </li>
    </ul>
  </div>

  <!-- <app-job-list [userInfo]="userInfo"></app-job-list> -->
</div>
