import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  registerMode = false;

  ngOnInit(): void {}

  registerToggle() {
    this.registerMode = !this.registerMode;
  }
}
<div class="container mt-5">
  <div [hidden]="!registerMode" class="flex items-center">
    <h1>Track your job search</h1>
    <p class="text-gray-400">Start a better way of tracking your job search.</p>

    <button
      (click)="registerToggle()"
      class="group relative h-12 w-48 overflow-hidden rounded-2xl bg-green-500 text-lg font-bold text-white"
    >
      Register
    </button>
    <div
      class="absolute inset-0 h-full w-full scale-0 rounded-2xl transition-all duration-300 group-hover:scale-100 group-hover:bg-white/30"
    ></div>
  </div>

  <div [hidden]="registerMode" class="container">
    <div class="flex content-center">
      <div>
        <app-register></app-register>
      </div>
    </div>
  </div>
</div>
