import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  registerMode = false;

  ngOnInit(): void {
    console.log(this.registerMode);
  }

  registerToggle() {
    this.registerMode = !this.registerMode;
  }
}
<div class="flex min-h-screen items-center justify-center">
  <div
    [hidden]="registerMode"
    class="container mx-auto flex flex-col-reverse p-6 lg:flex-row"
  >
    <p class="text-gray-400">Start a better way of tracking your job search.</p>

    <button
      (click)="registerToggle()"
      class="group relative h-12 w-48 overflow-hidden rounded-2xl bg-blue-500 text-lg font-bold text-white"
    >
      Register
    </button>
    <button
      (click)="registerToggle()"
      class="group relative h-12 w-48 overflow-hidden rounded-2xl bg-darkViolet text-lg font-bold text-white"
    >
      Learn More
    </button>

    <div
      class="absolute inset-0 h-full w-full scale-0 rounded-2xl transition-all duration-300 group-hover:scale-100 group-hover:bg-white/30"
    ></div>
  </div>

  <div [hidden]="!registerMode" class="container">
    <div class="flex content-center">
      <app-register></app-register>
    </div>
  </div>
</div>
