<header>
  <nav class="container relative mx-auto p-6">
    <div class="flex items-center justify-between">
      <div class="flex items-center space-x-20">
        <h2 class="text-2xl">Job Search Tracker</h2>
        <div class="hidden space-x-8 font-bold lg:flex"></div>
      </div>

      <!-- Right Side Links -->
      <div
        class="hidden items-center space-x-6 font-bold text-grayishViolet lg:flex"
      >
        <a href="#" class="hover:text-veryDarkViolet" [routerLink]="['/login']"
          >Login</a
        >
        <a
          href="#"
          class="rounded-full bg-cyan px-8 py-3 font-bold text-white hover:opacity-70"
          >Register</a
        >
      </div>
    </div>
  </nav>
</header>
