import { Component, Input, OnInit } from '@angular/core';
import { UserInfoService } from '../_services/user-info.service';
import { AccountService } from '../_services/account.service';
import { take } from 'rxjs';
import { User } from '../_models/user';
import { UserInfo } from '../_models/userInfo';
import { Job } from '../_models/job';

@Component({
  selector: 'app-job-list',
  templateUrl: './job-list.component.html',
  styleUrls: ['./job-list.component.css'],
})
export class JobListComponent implements OnInit {
  @Input() userInfo: UserInfo | undefined;
  //userInfo: UserInfo | undefined;
  user: User | null = null;
  jobs: Job[] = [];

  constructor(
    private accountService: AccountService,
    private userInfoService: UserInfoService
  ) {
    this.accountService.currentUser$.pipe(take(1)).subscribe({
      next: (user) => (this.user = user),
    });
  }

  ngOnInit(): void {}
}
<table class="tw-table-auto tw-border-collapse tw-border tw-border-slate-400">
  <thead>
    <tr>
      <th class="tw-border tw-border-slate-300">Job Title</th>
      <th>Job Description</th>
      <th>Company Name</th>
      <th>City</th>
      <th>State</th>
      <th>Skill Level</th>
      <th>Salary Range</th>
      <th>Remote/Hybrid/Onsite</th>
      <th>URL Listing</th>
      <th>Status</th>
    </tr>
  </thead>
  <tbody *ngIf="userInfo">
    <tr *ngFor="let job of userInfo.jobs">
      <td>{{ job.jobTitle }}</td>
      <td>{{ job.jobDescription }}</td>
      <td>{{ job.companyName }}</td>
      <td>{{ job.city }}</td>
      <td>{{ job.state }}</td>
      <td>{{ job.skillLevel }}</td>
      <td>{{ job.salaryRange }}</td>
      <td>{{ job.remoteHybridOnsite }}</td>
      <td>{{ job.urlListing }}</td>
      <td>{{ job.status }}</td>
    </tr>
  </tbody>
</table>
