import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { ToastrService } from 'ngx-toastr';
import { AccountService } from '../_services/account.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent implements OnInit {
  @Output() cancelRegister = new EventEmitter();
  model: any = {};

  constructor(
    private accountService: AccountService,
    private toastr: ToastrService
  ) {}

  ngOnInit(): void {}

  register() {
    this.accountService.register(this.model).subscribe({
      next: () => {
        this.cancel();
      },
      // error: (error) => this.toastr.error(error.error),
    });
  }

  cancel() {
    this.cancelRegister.emit(false);
  }
}
<div class="flex tw-items-center tw-bg-white lg:tw-ml-72 lg:tw-mt-12">
  <div class="tw-container tw-mx-auto tw-border">
    <div class="tw-my-50 tw-max-w-md">
      <div class="tw-text-center">
        <h1 class="tw-my-3 tw-text-3xl tw-font-semibold tw-text-gray-700">
          Register
        </h1>
      </div>
      <div class="tw-m-7">
        <form #registerForm="ngForm" (ngSubmit)="register()" autocomplete="off">
          <div class="tw-mb-6">
            <label
              for="email"
              class="tw-mb-2 tw-block tw-text-sm tw-text-gray-600"
              >User Name</label
            >
            <input
              type="text"
              name="username"
              [(ngModel)]="model.username"
              id="username"
              placeholder="username..."
              autocomplete="off"
              class="tw-w-full tw-rounded-md tw-border tw-border-gray-300 tw-px-3 tw-py-2 tw-placeholder-gray-300 focus:tw-border-indigo-300 focus:tw-outline-none focus:tw-ring focus:tw-ring-indigo-100"
            />
          </div>
          <div class="tw-mb-6">
            <div class="tw-mb-2 tw-flex tw-justify-between">
              <label for="password" class="tw-text-sm tw-text-gray-600"
                >Password</label
              >
            </div>
            <input
              type="password"
              name="password"
              [(ngModel)]="model.password"
              id="password"
              placeholder="Your Password"
              autocomplete="off"
              class="tw-w-full tw-rounded-md tw-border tw-border-gray-300 tw-px-3 tw-py-2 tw-placeholder-gray-300 focus:tw-border-indigo-300 focus:tw-outline-none focus:tw-ring focus:tw-ring-indigo-100"
            />
          </div>
          <button
            type="submit"
            class="tw-mt-1 tw-mb-5 tw-ml-2 tw-inline-block tw-w-full tw-rounded-lg tw-bg-blue-500 tw-py-2 tw-text-center tw-text-sm tw-font-semibold tw-text-white tw-shadow-sm tw-transition tw-duration-200 hover:tw-bg-blue-600 hover:tw-shadow-md focus:tw-bg-blue-700 focus:tw-shadow-sm focus:tw-ring-4 focus:tw-ring-blue-500 focus:tw-ring-opacity-50"
          >
            <span class="mr-2 inline-block">Sign Up</span>
          </button>
          <button
            type="button"
            class="tw-mt-1 tw-mb-5 tw-ml-2 tw-inline-block tw-w-full tw-rounded-lg tw-bg-blue-500 tw-py-2 tw-text-center tw-text-sm tw-font-semibold tw-text-white tw-shadow-sm tw-transition tw-duration-200 hover:tw-bg-blue-600 hover:tw-shadow-md focus:tw-bg-blue-700 focus:tw-shadow-sm focus:tw-ring-4 focus:tw-ring-blue-500 focus:tw-ring-opacity-50"
            (click)="cancel()"
          >
            <span class="mr-2 inline-block">Cancel</span>
          </button>
        </form>
      </div>
    </div>
  </div>
</div>
